import * as R from 'ramda';

export const getPhonesId = (state, id) => R.prop(id, state.phones);

export const getPhones = state => {
  const phones = R.map(id => getPhonesId(state, id), state.phonesPage.ids);
  return phones;
};

export const getRenderedPhonesLength = state => R.length(state.phonesPage.ids);
